package Com.Pattern;
//WAP to print Star right side triangle1
public class StarClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=5;i++){  
			for(int j=1;j<=i;j++){  
			        System.out.print("* ");  
			}  
			System.out.println();//new line  
			}  
		
	}

}
